from rest_framework import serializersfrom .models import User, Referralclass ReferralSerializers(serializers.ModelSerializer):    class Meta:        model = Referral        exclude = ['id']        unique_together = ['user', 'referral']class UserSerializers(serializers.ModelSerializer):    referral = serializers.StringRelatedField(many=True, read_only=True)    class Meta:        model = User        exclude = ['id', 'secret_code', ]