from django.urls import path, re_pathfrom drf_yasg import openapifrom drf_yasg.views import get_schema_viewfrom rest_framework import permissionsfrom . import viewsschema_view = get_schema_view(    openapi.Info(        title="Snippets API",        default_version='v1',        description="Test description",        terms_of_service="https://www.google.com/policies/terms/",        contact=openapi.Contact(email="contact@snippets.local"),        license=openapi.License(name="BSD License"),    ),    public=True,    permission_classes=[permissions.AllowAny],)urlpatterns = [    path('login/', views.UserViewSet.as_view({'post': 'login'})),    path('auth/', views.UserViewSet.as_view({'post': 'auth'})),    path('list/<int:id>/fill_invite_code/', views.ReferralViewSet.as_view({'post': 'fill_invite_code', })),    path('list/<int:id>/', views.UserViewSet.as_view({'get': 'list', })),    re_path(r'^swagger(?P<format>\.json|\.yaml)$', schema_view.without_ui(cache_timeout=0), name='schema-json'),    re_path(r'^swagger/$', schema_view.with_ui('swagger', cache_timeout=300), name='schema-swagger-ui'),    re_path(r'^redoc/$', schema_view.with_ui('redoc', cache_timeout=300), name='schema-redoc'),]